var typer = require('../typer.js');
var assert = require('assert');

assert.equal(typer('true'), true);
assert.equal(typer('false'), false);
assert.equal(typer('4'), 4);
assert.equal(typer('3.14'), 3.14);
assert.equal(typer('a string'), 'a string');
assert.equal(typeof(typer('{"an":"object"}')), 'object');
assert.equal(require('util').isArray(typer('a,b,c,d')), true);