module.exports = parseType;

function parseType (input) {

  // a number
  if (String(parseInt(input)) !== 'NaN') {
    return parseFloat(input);
  } else

  // a boolean 
  if (input === 'true') {
    return true;
  } else if (input === 'false') {
    return false;
  } else 

  // JSON
  if (input.match(/\{([\s|\S]*)\}/gi)) {
    return JSON.parse(input);
  } else 

  // an array
  if (input.match(/\,/gi)) {
    return input.split(',');
  } else {
    return String(input);
  }

}